import Vue from "vue";
import { createI18n } from "vue-i18n";
import messages from "./locales/index";

Vue.use(VueI18n);

export const i18n = createI18n({
  locale: "en", // set locale
  fallbackLocale: "es", // set fallback locale
  messages, // set locale messages
});
